import React, { useEffect, useState } from "react";
import Card from './Card';
import './App.css';
import albums from './albums.json';

const App = (props) => {
    const [albumList, setAlbumList] = useState([]);

    useEffect(() => {
        setAlbumList(albums);
    }, [albumList]);


  renderedList = () => {
    return this.state.albumList.map(
        (album) => {
            return (<Card key = {album.id} albumtitle = {album.title} albumdescription = {album.description} buttontext = "OK" imageurl = {album.image}/>);
        } );
    }
    return (
    <div>
        <div className="container">
            <SearchForm/>
        </div>
        <div className="container">{renderedList()}</div>
        </div>
        );
};

export default App;
